mapper.update:
update boke_sys_view
set id = #{id,jdbcType=BIGINT},
  gmt_created = #{gmtCreated,jdbcType=TIMESTAMP},
  gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
where ip = #{ip,jdbcType=VARCHAR}

mapper.select:
select id, ip, gmt_created, gmt_modified
from boke_sys_view
where ip = #{ip,jdbcType=VARCHAR}
